INSERT INTO `user`(`id`, `userName`, `password`, `identity`, `phone`, `name`, `avatar`, `email`, `motto`, `department`) VALUES (18989006, 'ssahis', 'T123456789', '学生', '17201506509', '王小虎', 'https://img2.woyaogexing.com/2022/10/09/a7d02a679efbe6be!400x400.jpg', 'dsadsa@.ccc', '宁可做个善良的人，坦荡一辈子。也不做个虚伪的人，算计一辈子。人活一世，睡能睡得舒坦，笑能笑得灿烂。就是赢了一辈子。', '2020级护理一班');
